jQuery(document).ready(function(){$('.login-form input[type="text"], .login-form input[type="password"], .login-form textarea').on("focus",function(){$(this).removeClass("input-error")});$(".login-form").on("submit",function(n){$(this).find('input[type="text"], input[type="password"], textarea').each(function(){$(this).val()==""?(n.preventDefault(),$(this).addClass("input-error")):$(this).removeClass("input-error")})})});